import React, { useState } from "react";
import styled from "styled-components";

// 스타일 정의
const ButtonWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 40px;
`;

const SearchContainer = styled.div`
  display: ${({ isClicked }) => (isClicked ? "flex" : "none")};
  align-items: center;
  width: 400px;
  height: 50px;
  background-color: #fff;
  border: 2px solid #ddd;
  border-radius: 25px;
  overflow: hidden;
  transition: border-color 0.3s ease;

  &:focus-within {
    border-color: #4096ff;
  }
`;

const Button = styled.button`
  width: ${({ isClicked }) => (isClicked ? "0" : "200px")};
  height: 50px;
  background-color: #fff;
  color: #000;
  border: none;
  border-radius: 25px;
  cursor: pointer;
  font-size: 1rem;
  transition: width 0.3s ease, background-color 0.3s ease;
  overflow: hidden;

  &:hover {
    background-color: ${({ isClicked }) => (isClicked ? "transparent" : "#ddd")};
  }
`;

const Input = styled.input`
  flex: 1;
  height: 100%;
  border: none;
  padding: 0 15px;
  font-size: 1rem;
  font-family: "Amethysta", serif;
  outline: none;

  &::placeholder {
    color: #aaa;
    font-style: italic;
  }
`;

const SearchButton = styled.button`
  height: 100%;
  padding: 0 20px;
  background-color: #4096ff;
  color: #fff;
  border: none;
  font-size: 1rem;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #307acc;
  }
`;

function SearchButtonComponent() {
  const [isClicked, setIsClicked] = useState(false);
  const [searchText, setSearchText] = useState("");

  const handleClick = () => {
    setIsClicked(true);
  };

  const handleChange = (e) => {
    setSearchText(e.target.value);
  };

  const handleSearch = () => {
    alert(`검색: ${searchText}`);
  };

  return (
    <ButtonWrapper>
      {/* 처음 시작 버튼 */}
      <Button isClicked={isClicked} onMouseOver={handleClick}>
        {isClicked ? null : "시작하기"}
      </Button>

      {/* 검색창 */}
      <SearchContainer isClicked={isClicked}>
        <Input
          type="text"
          value={searchText}
          onChange={handleChange}
          autoFocus={isClicked}
          placeholder="검색어를 입력하세요"
        />
        <SearchButton onClick={handleSearch}>검색</SearchButton>
      </SearchContainer>
    </ButtonWrapper>
  );
}

export default SearchButtonComponent;
