import React, { useState } from "react";
import styled from "styled-components";

// 스타일 정의
const ButtonWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 40px;
`;

const SearchButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: ${({ isClicked }) => (isClicked ? "space-between" : "center")};
  width: ${({ isClicked }) => (isClicked ? "400px" : "200px")};
  height: 50px;
  background-color: #fff;
  color: #000;
  border: none;
  border-radius: 25px;
  cursor: pointer;
  font-size: 1rem;
  font-weight: bold;
  transition: width 0.3s ease, background-color 0.3s ease;
  padding: ${({ isClicked }) => (isClicked ? "0 10px" : "0")};
  overflow: hidden;

  &:hover {
    background-color: ${({ isClicked }) => (isClicked ? "#fff" : "#ddd")};
  }
`;

const Input = styled.input`
  flex: 1;
  height: 80%; /* 버튼 높이에 맞게 조정 */
  border: none;
  border-radius: 25px;
  padding: 0 10px;
  font-size: 1rem;
  font-family: "Amethysta", serif;
  outline: none;
  opacity: ${({ isClicked }) => (isClicked ? "1" : "0")};
  transition: opacity 0.3s ease;
  pointer-events: ${({ isClicked }) => (isClicked ? "all" : "none")};

  &::placeholder {
    color: #aaa;
    font-style: italic;
  }
`;

const SearchIcon = styled.button`
  height: 80%;
  padding: 0 15px;
  background-color: #4096ff;
  color: #fff;
  border: none;
  border-radius: 25px;
  font-size: 1rem;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.3s ease;
  opacity: ${({ isClicked }) => (isClicked ? "1" : "0")};
  pointer-events: ${({ isClicked }) => (isClicked ? "all" : "none")};

  &:hover {
    background-color: #307acc;
  }
`;

function SearchButtonComponent() {
  const [isClicked, setIsClicked] = useState(false);
  const [searchText, setSearchText] = useState("");

  const handleClick = () => {
    setIsClicked(true);
  };

  const handleChange = (e) => {
    setSearchText(e.target.value);
  };

  const handleSearch = () => {
    alert(`검색: ${searchText}`);
  };

  return (
    <ButtonWrapper>
      <SearchButton
        isClicked={isClicked}
        onMouseOver={handleClick}
        onClick={!isClicked ? handleClick : undefined}
      >
        {!isClicked && "시작하기"} {/* 버튼 텍스트 */}
        <Input
          isClicked={isClicked}
          type="text"
          value={searchText}
          onChange={handleChange}
          autoFocus={isClicked}
          placeholder="검색어를 입력하세요"
        />
        <SearchIcon isClicked={isClicked} onClick={handleSearch}>
          검색
        </SearchIcon>
      </SearchButton>
    </ButtonWrapper>
  );
}

export default SearchButtonComponent;
