import React, { useState } from 'react';
import { Card, Flex, Typography, Input, Button } from 'antd';

const cardStyle = {
  maxWidth: 1034,
  width: '100%',
  margin: '0 auto',
  padding: 16,
  backgroundColor: 'black',
  border: 'none',
};

const inputStyle = {
  backgroundColor: '#333',
  color: '#fff',
  border: '1px solid #555',
  borderRadius: '4px',
  padding: '8px',
};

const CommentBox = () => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  const handleAddComment = () => {
    if (newComment.trim()) {
      setComments([...comments, newComment]);
      setNewComment('');
    }
  };

  return (
    <Card style={cardStyle}>
      <Typography.Title level={4} style={{ color: '#fff', marginBottom: '16px' }}>
        댓글
      </Typography.Title>
      <div style={{ marginBottom: '16px' }}>
        {comments.map((comment, index) => (
          <Card
            key={index}
            style={{
              backgroundColor: '#222',
              border: 'none',
              marginBottom: '8px',
              padding: '8px',
            }}
          >
            <Typography.Text style={{ color: '#fff' }}>{comment}</Typography.Text>
          </Card>
        ))}
      </div>
      <Flex justify="space-between" align="center">
        <Input
          style={inputStyle}
          value={newComment}
          placeholder="댓글을 입력하세요"
          onChange={(e) => setNewComment(e.target.value)}
        />
        <Button type="primary" style={{ marginLeft: '8px' }} onClick={handleAddComment}>
          등록
        </Button>
      </Flex>
    </Card>
  );
};

export default CommentBox;
