const NavList = styled.ul`
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
`;

const NavItem = styled.li`
  display: flex; /* 플렉스 박스 사용 */
  align-items: center; /* 세로 가운데 정렬 */
  margin: 0 16px;
  color: #fff;
  cursor: pointer;
  font-size: 1rem;
  transition: color 0.3s;

  &:hover {
    color: #4096ff;
  }

  /* 가상 요소로 구분선 추가 */
  &::after {
    content: "|";
    color: #fff;
    margin-left: 16px;
  }

  /* 마지막 항목은 구분선 제거 */
  &:last-child::after {
    content: "";
  }
`;
