import React from "react";
import styled from "styled-components";
import { Form, Input, Button, Typography } from "antd";

const { Link } = Typography;

// 스타일 정의
const FormContainer = styled.div`
  width: 726px;
  height: 726px;
  background-color: #858585;
  padding: 30px 100px;
  border-radius: 8px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start; /* 타이틀을 위로 */
  align-items: center;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const TitleWrapper = styled.div`
  margin-top: 20px; /* 컨테이너 상단에서 약간의 여백 */
  margin-bottom: 30px; /* 로그인 폼과의 간격 */
  text-align: center;
`;

const Title = styled.h1`
  font-size: 48px;
  font-weight: bold;
  color: #fff;
  margin: 0;
`;

const Subtitle = styled.p`
  font-size: 16px;
  color: #fff;
  margin: 5px 0 0 0; /* 위에 약간의 간격 */
`;

const Line = styled.hr`
  width: 80%;
  border: none;
  border-top: 2px solid #ccc;
  margin: 20px 0;
`;

const StyledForm = styled(Form)`
  width: 100%;
`;

const FormTitle = styled.h2`
  font-size: 24px;
  text-align: center;
  margin-bottom: 20px;
  color: #333;
`;

const StyledButton = styled(Button)`
  width: 100%;
  background-color: #4096ff;
  color: white;
  font-weight: bold;
  height: 40px;

  &:hover {
    background-color: #307acc;
    color: white;
  }
`;

const LinkWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 10px;
`;

function LoginBox() {
  const handleSubmit = (values) => {
    console.log("Login Form Submitted:", values);
  };

  return (
    <FormContainer>
      {/* Title Section */}
      <TitleWrapper>
      <Subtitle>MZ세대의 언어로 가득 찬 힙한 언어사전</Subtitle>
        <Title>WELCOME</Title>

      </TitleWrapper>

      {/* 구분선 */}
      <Line />

      <StyledForm
        name="loginForm"
        initialValues={{ remember: true }}
        onFinish={handleSubmit}
      >
        <FormTitle>로그인</FormTitle>

        {/* ID Input */}
        <Form.Item
          name="id"
          rules={[{ required: true, message: "아이디를 입력하세요." }]}
        >
          <Input placeholder="아이디" size="large" />
        </Form.Item>

        {/* Password Input */}
        <Form.Item
          name="password"
          rules={[{ required: true, message: "비밀번호를 입력하세요." }]}
        >
          <Input.Password placeholder="비밀번호" size="large" />
        </Form.Item>

        {/* Login Button */}
        <Form.Item>
          <StyledButton type="primary" htmlType="submit">
            로그인
          </StyledButton>
        </Form.Item>

        {/* Links */}
        <LinkWrapper>
          <Link href="#">아이디 찾기</Link>
          <Link href="#">비밀번호 찾기</Link>
        </LinkWrapper>
      </StyledForm>
    </FormContainer>
  );
}

export default LoginBox;
