import React, { useState } from "react";
import styled from "styled-components";

// 스타일 정의
const ButtonWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Button = styled.button`
  width: ${({ isClicked }) => (isClicked ? "400px" : "200px")};
  height: 50px;
  background-color: #fff;
  color: #000;
  border: none;
  border-radius: 20px;
  cursor: pointer;
  font-size: 1rem;
  font-family: 'Amethysta', serif;
  transition: width 0.3s ease, background-color 0.3s ease; /* 부드럽게 크기와 색상 변화 */
  margin-top: 40px;

  ${({ isClicked }) =>
    isClicked &&
    `
    display: flex;
    justify-content: space-between;
    padding: 0 10px;
    border: 2px solid #ddd;
  `}

  &:hover {
    background-color: #ddd;
  }
`;

const Input = styled.input`
  width: 100%;
  height: 80%; /* 버튼 높이에 맞게 조정 */
  border: none;
  border-radius: 20px;
  padding: 0 10px;
  font-size: 1rem;
  font-family: 'Amethysta', serif;
  outline: none;
`;

function SearchButton() {
  const [isClicked, setIsClicked] = useState(false);
  const [searchText, setSearchText] = useState("");

  const handleClick = () => {
    setIsClicked(true);
  };

  const handleChange = (e) => {
    setSearchText(e.target.value);
  };

  return (
    <ButtonWrapper>
      <Button isClicked={isClicked} onMouseOver={handleClick}>
        {isClicked ? (
          <Input
            type="text"
            value={searchText}
            onChange={handleChange}
            autoFocus
            placeholder="검색어를 입력하세요"
          />
        ) : (
          "시작하기"
        )}
      </Button>
    </ButtonWrapper>
  );
}

export default SearchButton;
