import React from "react";
import { Layout } from "antd";
import Header from "../components/Header"; // 분리된 헤더 컴포넌트 import

const { Footer, Content } = Layout;

const layoutStyle = {
  minHeight: "100vh", // Layout이 화면 전체 높이를 차지
};

const contentStyle = {
  textAlign: "center",
  flex: 1, // flex-grow로 남은 공간 차지
  lineHeight: "120px",
  color: "#fff",
  backgroundColor: "#0958d9",
};

const footerStyle = {
  textAlign: "center",
  color: "#fff",
  backgroundColor: "#4096ff",
};

const App = () => (
  <Layout style={layoutStyle}>
    {/* 커스텀 Header 컴포넌트 */}
    <Header />

    <Content style={contentStyle}>Content</Content>
    <Footer style={footerStyle}>Footer</Footer>
  </Layout>
);

export default App;
